package com.mapbox.search.sample.advanced

import android.Manifest
import android.content.Context
import android.content.pm.PackageManager
import android.os.Bundle
import android.util.Log
import android.view.View
import androidx.appcompat.app.AppCompatActivity
import androidx.core.app.ActivityCompat
import androidx.core.content.ContextCompat
import androidx.lifecycle.lifecycleScope
import com.mapbox.geojson.utils.PolylineUtils
import com.mapbox.maps.MapView
import com.mapbox.maps.Style
import com.mapbox.navigation.ev.ExperimentalPreviewMapboxEvAPI
import com.mapbox.search.CategorySearchOptions
import com.mapbox.search.RouteOptions
import com.mapbox.search.base.ExperimentalMapboxSearchAPI
import com.mapbox.search.extensions.AdvancedCategorySearchEngine
import com.mapbox.search.sample.R
import com.mapbox.turf.TurfTransformation
import java.util.concurrent.TimeUnit

class AdvancedSearchEngineActivity : AppCompatActivity() {

    private lateinit var mapView: MapView
    private lateinit var mapMarkersManager: AdvancedSearchMapAnnotationManager

    private lateinit var advancedSearchEngine: AdvancedCategorySearchEngine

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_advanced_search)

        advancedSearchEngine = AdvancedCategorySearchEngine.createSearchEngine()

        mapView = findViewById(R.id.map_view)
        mapMarkersManager = AdvancedSearchMapAnnotationManager(mapView)

        mapView.mapboxMap.loadStyle(Style.MAPBOX_STREETS)

        findViewById<View>(R.id.search).setOnClickListener { search() }

        if (!isPermissionGranted(Manifest.permission.ACCESS_FINE_LOCATION)) {
            ActivityCompat.requestPermissions(
                this,
                arrayOf(
                    Manifest.permission.ACCESS_FINE_LOCATION,
                    Manifest.permission.ACCESS_COARSE_LOCATION
                ),
                PERMISSIONS_REQUEST_LOCATION
            )
        }
    }

    private fun search() {
        val originalRoute = PolylineUtils.decode(TEST_ROUTE, 6)
        val route = TurfTransformation.simplify(originalRoute, 0.01, true)
        mapMarkersManager.showRoute(route)

        val categorySearchOptions = CategorySearchOptions(
            routeOptions = RouteOptions(
                route,
                RouteOptions.Deviation.Time(30, TimeUnit.MINUTES)
            )
        )

        lifecycleScope.launchWhenStarted {
            val result = advancedSearchEngine.search(
                listOf("cafe", "charging_station"),
                categorySearchOptions,
            )

            result.onSuccess { results ->
                Log.d(LOG_TAG, "Search results: $results")
                mapMarkersManager.showMarkers(results.map { it.coordinate })
            }.onFailure {
                Log.d(LOG_TAG, "Search error", it)
            }
        }
    }

